mapper.select:
select

from user_energy_expire_time where uid =#{uid} and deleted = 0 and DATE_FORMAT(end_time,'%y%m%d') =
DATE_FORMAT(now(),'%y%m%d') order by start_time

select

from user_energy_expire_time where uid =#{uid} and deleted = 0 and DATE_FORMAT(end_time,'%y%m%d') =
DATE_FORMAT( DATE_SUB(now(), INTERVAL 1 DAY ), '%y%m%d' ) order by start_time
